import React, { Component } from "react";
import DetailIages from "./Components/DetailImages";
import ProductInfo from "./Components/ProductInfo";
import RecommendItem from "./Components/Recommand/RecommenItem";
import ShoppingBasket from "./Components/ShoppingBasket";
import "./ItemDetail.scss";

class ItemDetail extends Component {
  constructor() {
    super();
    this.state = {
      detailImage: [],
      modal: false,
    };
  }

  handleModal = () => {
    const { modal } = this.state;
    this.setState({
      modal: !modal,
    });
  };

  componentDidMount() {
    fetch("/Data/itemDetail/DetailData.json")
      .then((res) => res.json())
      .then((result) => {
        this.setState({ detailImage: result.image });
      });
  }

  render() {
    const { detailImage, modal } = this.state;
    console.log(Image);
    return (
      <article className="ItemDetail">
        <DetailIages
          handleModal={this.handleModal}
          detailImage={detailImage}
          modal={modal}
        />
        <ProductInfo detailImage={detailImage} />
        <RecommendItem />
      </article>
    );
  }
}

export default ItemDetail;
