import React, { Component } from "react";
import "./SizeBox.scss";

class SizeBox extends Component {
  constructor() {
    super();
    this.state = {
      size: [],
    };
  }

  componentDidMount() {
    fetch("/Data/itemDetail/DetailData.json", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          size: result.size,
        });
      });
  }
  render() {
    const { size } = this.state;
    return (
      <div className="SizeBox">
        {size.map((el) => (
          <div className={el.isActive ? "eachSize" : "disabledSize"}>
            <button>{el.size}</button>
          </div>
        ))}
      </div>
    );
  }
}

export default SizeBox;
