import React, { Component } from "react";
import "./Login.scss";

class Login extends Component {
  constructor() {
    super();
    this.state = {
      userId: "",
      userPw: "",
      isIdFail: false,
      isPwFail: false,
    };
  }

  userInput = (e) => {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  };

  login = (e) => {
    e.preventDefault();
    const { userId, userPw } = this.state;
    if (userId.length === 0) {
      this.setState({
        isIdFail: true,
      });
    }
    if (userPw.length === 0) {
      this.setState({
        isPwFail: true,
      });
    }
  };

  render() {
    const { isIdFail, isPwFail } = this.state;
    // console.log(console.log(this.state.userId, this.state.userPw));
    return (
      <div className={this.props.isLogin ? "loginModal" : "loginModalHide"}>
        <div className="modal">
          <div className="closeIcon" onClick={this.props.handleClose}>
            <i class="fas fa-times"></i>
          </div>
          <div className="loginLogo">
            <img
              className="loginNikeLogo"
              src="./images/nikeMainLogo.svg"
              alt="메인 나이키 로고"
            />
            <h2 className="loginNikeText">나이키 로그인</h2>
          </div>
          <div className="loginForm">
            <form>
              <span className="loginFailMsg">
                아이디 혹은 비밀번호가 잘못 입력되었습니다.
              </span>
              <input
                className="loginIdBox"
                placeholder="아이디"
                name="userId"
                onChange={this.userInput}
              ></input>
              <span
                className={
                  isIdFail ? "lhideCl oginIdFailMsg" : "loginIdFailMsg lhideCl"
                }
              >
                필수 입력 항목입니다.
              </span>
              <input
                className="loginIdBox"
                placeholder="비밀번호"
                name="userPw"
                onChange={this.userInput}
              ></input>
              <span className="loginPwFailMsg">필수 입력 항목입니다.</span>
              <div className="loginOpt">
                <div className="loginConBox">
                  <input className="checkbox" type="checkbox"></input>
                  <span className="loginCon">로그인 유지하기</span>
                </div>
                <span className="findId">아이디/비밀번호 찾기</span>
              </div>
              <div className="btnBox">
                <button class="loginBtn" onClick={this.login}>
                  로그인
                </button>
                <button class="kaloginBtn">
                  <img
                    className="kakaoLogo"
                    src="./images/kakaotalk.svg"
                    alt="카카오톡 이미지"
                  />
                  카카오계정 로그인
                </button>
                <button class="faloginBtn">
                  <img
                    className="faceBookLogo"
                    src="./images/faceBook.svg"
                    alt="페이스북 이미지"
                  />
                  페이스북으로 로그인
                </button>
              </div>
              <div className="loginFooter">
                <div className="loginSignupBox">
                  <span className="loginSignupText">회원이 아니신가요?</span>
                  <span className="loginSignupBtn">회원가입</span>
                </div>
                <span className="unknownUser">비회원 주문 조회</span>
              </div>
            </form>
          </div>
        </div>

        <div className="modalLayer" onClick={this.props.handleClose}></div>
      </div>
    );
  }
}

export default Login;
