import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import Nav from "../../Components/Nav/Nav";
import PersonalInfo from "./Components/PersonalInfo";
import TermsInfo from "./Components/TermsInfo";
import SelectInfo from "./Components/SelectInfo";
import "./SignUp.scss";

class SignUp extends Component {
  state = {
    userEmail: "",
    userPassword: "",
    userRePassword: "",
    userName: "",
    userPhone: "",

    userEmailActive: false,
    userPasswordActive: false,
    userRePasswordActive: false,
    userNameActive: false,
    userPhoneActive: false,

    userEmailError: false,
    userPasswordError: false,
    userRePasswordError: false,
    userNameError: false,
    userPhoneError: false,
  };

  focusTextInput = this.focusTextInput.bind(this);

  focusTextInput() {
    const {
      userEmailActive,
      userPasswordActive,
      userRePasswordActive,
      userNameActive,
      userPhoneActive,
      termsCheck,
      personalCheck,
    } = this.state;

    if (userEmailActive) {
      return this.idInput.focus();
    }

    if (userPasswordActive) {
      return this.pwInput.focus();
    }
    if (userRePasswordActive) {
      return this.repwInput.focus();
    }
    if (userNameActive) {
      window.scrollTo(0, 300);
      return this.nameInput.focus();
    }

    if (userPhoneActive) {
      this.phoneInput.focus();
      return window.scrollTo(0, 300);
    }

    if (!termsCheck) {
      return window.scrollTo(0, 335);
    }

    if (!personalCheck) {
      return window.scrollTo(0, 700);
    } else {
      window.scrollTo(0, 335);
      this.idInput.focus();
    }
  }

  errorPrint = (inputError) => {
    this.setState({
      [`${inputError}Active`]: true,
      [`${inputError}Error`]: true,
    });
  };

  errorClose = (inputError) => {
    this.setState({
      [`${inputError}Active`]: false,
    });
  };

  emailCheck = (inputError, inputValue) => {
    const emailError =
      inputValue.includes("@") &&
      inputValue.includes(".") &&
      inputValue.includes("com");
    const checkKor = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣()]/;

    if (emailError && !inputValue.match(checkKor)) {
      this.errorClose(inputError);
    } else {
      this.errorPrint(inputError);
    }
  };

  pwCheck = (inputError, inputValue) => {
    const checkSpc = /[~!@#$%^&*_+|<>?:{}]/;
    const checkKor = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣()]/;
    const checkEng = /[a-zA-Z]/;
    const checkNum = /[0-9]/;

    if (8 > inputValue.length || 16 < inputValue.length) {
      return this.errorPrint(inputError);
    } else {
      if (
        inputValue.match(checkSpc) &&
        inputValue.match(checkEng) &&
        inputValue.match(checkNum) &&
        !inputValue.match(checkKor)
      ) {
        return this.errorClose(inputError);
      } else {
        return this.errorPrint(inputError);
      }
    }
  };

  repwCheck = (inputError, inputValue) => {
    const { userPassword } = this.state;

    if (inputValue === userPassword) {
      return this.errorClose(inputError);
    } else {
      return this.errorPrint(inputError);
    }
  };

  nameCheck = (inputError, inputValue) => {
    const checkSpc = /[~!@#$%^&*_+|<>?:{}]/;
    const checkKor = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣()]/;
    const checkEng = /[a-zA-Z]/;
    const checkNum = /[0-9]/;

    if (
      !inputValue.match(checkSpc) &&
      !inputValue.match(checkEng) &&
      !inputValue.match(checkNum) &&
      inputValue.match(checkKor)
    ) {
      return this.errorClose(inputError);
    } else {
      return this.errorPrint(inputError);
    }
  };

  phoneCheck = (inputError, inputValue) => {
    const checkSpc = /[~!@#$%^&*_+|<>?:{}]/;
    const checkKor = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣()]/;
    const checkEng = /[a-zA-Z]/;
    const checkNum = /[0-9]/;

    if (
      !inputValue.match(checkSpc) &&
      !inputValue.match(checkEng) &&
      inputValue.match(checkNum) &&
      !inputValue.match(checkKor) &&
      inputValue.length === 11
    ) {
      return this.errorClose(inputError);
    } else {
      return this.errorPrint(inputError);
    }
  };

  handleActive = (inputError, inputValue) => {
    const basicError = inputValue.length;

    if (basicError === 0) {
      this.setState({
        [`${inputError}Active`]: true,
        [`${inputError}Error`]: false,
      });
    } else if (basicError > 0) {
      if (inputError === "userEmail") {
        return this.emailCheck(inputError, inputValue);
      }

      if (inputError === "userPassword") {
        return this.pwCheck(inputError, inputValue);
      }

      if (inputError === "userRePassword") {
        return this.repwCheck(inputError, inputValue);
      }

      if (inputError === "userName") {
        return this.nameCheck(inputError, inputValue);
      }

      if (inputError === "userPhone") {
        return this.phoneCheck(inputError, inputValue);
      }
    }
  };

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState(
      {
        [name]: value,
      },
      () => {
        this.handleActive(name, value);
      }
    );
  };

  handleClick = (e) => {
    const { name, checked } = e.target;

    this.setState({
      [name]: checked,
    });
  };

  handleSignUp = () => {
    const {
      userEmail,
      userPassword,
      userRePassword,
      userName,
      userPhone,
      userEmailActive,
      userPasswordActive,
      userRePasswordActive,
      userNameActive,
      userPhoneActive,
    } = this.state;

    const { termsCheck, personalCheck } = this.state;

    if (termsCheck === true && personalCheck === true) {
      this.handleActive("userEmail", userEmail);
      this.handleActive("userPassword", userPassword);
      this.handleActive("userRePassword", userRePassword);
      this.handleActive("userName", userName);
      this.handleActive("userPhone", userPhone);

      if (
        !userEmailActive &&
        !userPasswordActive &&
        !userRePasswordActive &&
        !userNameActive &&
        !userPhoneActive &&
        userEmail !== "" &&
        userPassword !== "" &&
        userRePassword !== "" &&
        userName !== "" &&
        userPhone !== ""
      ) {
        console.log("로딩중");
        console.log(userEmail, userPassword, userName);
        fetch("http://10.58.3.208:8000/users/signup", {
          method: "POST",
          body: JSON.stringify({
            email: userEmail,
            password: userPassword,
            name: userName,
            phone_number: userPhone,
          }),
        }).then((response) => {
          // console.log(response.json());

          // .then((res) => console.log(res));
          // .then((response) => response.json())
          // .then((result) => {
          //   console.log(result);
          if (response.status === 200) {
            console.log(response);
            alert("로그인 성공");
            this.props.history.push("/");
            // console.log(response.json());
          } else if (response.status === 400) {
            console.log(response);
            alert("중복된 이메일이 있습니다.");
          }
        });
        // .then((response) => {
        //   // .then((res) => console.log(res));
        //   // .then((response) => response.json())
        //   // .then((result) => {
        //   //   console.log(result);
        //   if (response.status === 200) {
        //     alert("로그인 성공");
        //     this.props.history.push("/");
        //   } else if (response.status === 400) {
        //     alert("중복된 이메일이 있습니다.");
        //   }
        // });
        // });
      } else {
        this.focusTextInput();
        alert("입력 사항을 입력해주세요.");
      }
    } else {
      this.handleActive("userEmail", userEmail);
      this.handleActive("userPassword", userPassword);
      this.handleActive("userRePassword", userRePassword);
      this.handleActive("userName", userName);
      this.handleActive("userPhone", userPhone);
      this.focusTextInput();
      alert("필수 체크란을 확인하세요.");
    }
  };

  render() {
    const {
      userEmailActive,
      userPasswordActive,
      userRePasswordActive,
      userNameActive,
      userPhoneActive,
      userEmailError,
      userPasswordError,
      userRePasswordError,
      userNameError,
      userPhoneError,
    } = this.state;

    return (
      <div className="SignUp">
        <Nav />
        <section className="section">
          <div className="signUpCenter">
            <div className="signUpText">
              <h2>나이키 멤버 가입</h2>
              <p>
                멤버가 되어 나이키가 제공하는<br></br> 최고의 제품과 혜택을
                만나보세요.
              </p>
            </div>
            <div className="coSignConnect">
              <span className="kakaoSignUp">
                <img
                  className="kakaoLogo"
                  src="./images/Nav/kakaotalk.svg"
                  alt="카카오톡 이미지"
                />
                카카오 계정으로 신규 가입
              </span>
              <span className="faceBookSignUp">
                <img
                  className="faceBookLogo"
                  src="./images/Nav/faceBook.svg"
                  alt="페이스북 이미지"
                />
                페이스북 계정으로 신규 가입
              </span>
            </div>
            <div className="orContainer">
              <div className="orLine"></div>
              <span className="orText">OR</span>
              <div className="orLine"></div>
            </div>
            <div className="infoContainer">
              <form className="infoForm">
                <div className="inputCenter">
                  <input
                    className={userEmailActive ? "inputBoxRed" : "inputBox"}
                    placeholder="사용하실 ID를 입력해주세요. (수신 가능 E-mail)"
                    name="userEmail"
                    onChange={this.handleChange}
                    ref={(input) => {
                      this.idInput = input;
                    }}
                  ></input>
                  <span
                    className={userEmailActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {userEmailError
                      ? "이메일 형태로 입력해주세요. 해당 계정으로 주문 내역이 발송됩니다."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={userPasswordActive ? "inputBoxRed" : "inputBox"}
                    placeholder="영문+숫자+특수문자 8~16자리(특수문자 괄호()는 사용 불가)"
                    name="userPassword"
                    onChange={this.handleChange}
                    type="password"
                    ref={(input) => {
                      this.pwInput = input;
                    }}
                  ></input>
                  <span
                    className={userPasswordActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {userPasswordError
                      ? "영문/숫자/특수문자 조합 8~16자 조합으로 입력해주세요."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={
                      userRePasswordActive ? "inputBoxRed" : "inputBox"
                    }
                    placeholder="패스워드를 다시 입력해주세요."
                    name="userRePassword"
                    onChange={this.handleChange}
                    type="password"
                    ref={(input) => {
                      this.repwInput = input;
                    }}
                  ></input>
                  <span
                    className={
                      userRePasswordActive ? "errorMsg" : "errorMsgNone"
                    }
                  >
                    {userRePasswordError
                      ? "입력값이 일차하지 않습니다."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={userNameActive ? "inputBoxRed" : "inputBox"}
                    placeholder="이름을 입력해 주세요."
                    name="userName"
                    onChange={this.handleChange}
                    ref={(input) => {
                      this.nameInput = input;
                    }}
                  ></input>
                  <span
                    className={userNameActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {userNameError
                      ? "한글만 입력 가능합니다."
