import React, { Component } from "react";
import "./ItemInfo.scss";

class ItemInfo extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      content: "",
      styleNcolor: {},
    };
  }

  componentDidMount() {
    fetch("/Data/itemDetail/DetailData.json")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          title: result.itemInfo.title,
          content: result.itemInfo.content,
          styleNcolor: result.itemInfo.styleNcolor,
        });
      });
  }

  componentDidUpdate(prevProps, prevState) {
    const { styleNcolor } = this.state;
    const { handleStyle } = this.props;
    if (prevState.styleNcolor !== styleNcolor) {
      handleStyle(styleNcolor.style);
    }
  }

  render() {
    const { title, content, styleNcolor } = this.state;
    return (
      <div className="ItemInfo">
        <div className="mainCopy">{title}</div>
        <div className="content">{content}</div>
        <div className="colorNstyle">
          <span className="color">현재 컬러 : {styleNcolor.color}</span>
          <span className="style">스타일 : {styleNcolor.style}</span>
          <span>더 보기 </span>
        </div>
      </div>
    );
  }
}

export default ItemInfo;
