import React, { Component } from "react";
import Nav from "../../Components/Nav/Nav";
import PersonalInfo from "./Components/PersonalInfo";
import TermsInfo from "./Components/TermsInfo";
import SelectInfo from "./Components/SelectInfo";

import "./SignUp.scss";

class SignUp extends Component {
  state = {
    userEmail: "",
    userPassword: "",
    userRePassword: "",
    userName: "",
    userPhone: "",
    userEmailActive: false,
    userPasswordActive: false,
    userRePasswordActive: false,
    userNameActive: false,
    userPhoneActive: false,
    param: "",
    // param: false,
  };

  handleActive = (inputError) => {
    // const {
    //   userEmail,
    //   userPassword,
    //   userRePassword,
    //   userName,
    //   userPhone,
    //   emailActive,
    // } = this.state;

    const basicError = inputError.length;
    console.log("test", basicError);

    if (basicError !== 0) {
      this.setState({
        [`${inputError}Active`]: true,
      });
      console.log([`${inputError}Active`]);
    }
  };

  handleChange = (e) => {
    const {
      userEmail,
      userPassword,
      userRePassword,
      userName,
      userPhone,
      emailActive,
    } = this.state;
    const { name, value } = e.target;
    console.log(name, value);
    this.setState(
      {
        [name]: value,
      },
      () => {
        this.handleActive(name);
      }
    );
    console.log(name);
  };

  render() {
    const {
      userEmail,
      userPassword,
      userRePassword,
      userName,
      userPhone,
    } = this.state;

    return (
      <div className="SignUp">
        <Nav />
        <section className="section">
          <div className="signUpCenter">
            <div className="signUpText">
              <h2>나이키 멤버 가입</h2>
              <p>
                멤버가 되어 나이키가 제공하는<br></br> 최고의 제품과 혜택을
                만나보세요.
              </p>
            </div>
            <div className="coSignConnect">
              <span className="kakaoSignUp">
                <img
                  className="kakaoLogo"
                  src="./images/kakaotalk.svg"
                  alt="카카오톡 이미지"
                />
                카카오 계정으로 신규 가입
              </span>
              <span className="faceBookSignUp">
                <img
                  className="faceBookLogo"
                  src="./images/faceBook.svg"
                  alt="페이스북 이미지"
                />
                페이스북 계정으로 신규 가입
              </span>
            </div>
            <div className="orContainer">
              <div className="orLine"></div>
              <span className="orText">OR</span>
              <div className="orLine"></div>
            </div>
            <div className="infoContainer">
              <form className="infoForm">
                <div className="inputCenter">
                  <input
                    className={
                      this.state.userEmailActive ? "inputBoxRed" : "inputBox"
                    }
                    placeholder="사용하실 ID를 입력해주세요. (수신 가능 E-mail)"
                    name="userEmail"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={
                      this.state.userEmailActive ? "errorMsg" : "errorMsgNone"
                    }
                  >
                    필수 입력 항목입니다.
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={
                      this.state.userPasswordActive ? "inputBoxRed" : "inputBox"
                    }
                    placeholder="영문+숫자+특수문자 8~16자리(특수문자 괄호()는 사용 불가)"
                    name="userPassword"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={
                      this.state.userPasswordActive
                        ? "errorMsg"
                        : "errorMsgNone"
                    }
                  >
                    필수 입력 항목입니다.
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={
                      this.state.userRePasswordActive
                        ? "inputBoxRed"
                        : "inputBox"
                    }
                    placeholder="패스워드를 다시 입력해주세요."
                    name="userRePassword"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={
                      this.state.userRePasswordActive
                        ? "errorMsg"
                        : "errorMsgNone"
                    }
                  >
                    필수 입력 항목입니다.
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={
                      this.state.userNameActive ? "inputBoxRed" : "inputBox"
                    }
                    placeholder="이름을 입력해 주세요."
                    name="userName"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={
                      this.state.userNameActive ? "errorMsg" : "errorMsgNone"
                    }
                  >
                    필수 입력 항목입니다.
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={
                      this.state.userPhoneActive ? "inputBoxRed" : "inputBox"
                    }
                    placeholder="휴대폰 번호 '-'표 없이 입력해 주세요."
                    name="userPhone"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={
                      this.state.userPhoneActive ? "errorMsg" : "errorMsgNone"
                    }
                  >
                    필수 입력 항목입니다.
                  </span>
                </div>
                <TermsInfo />
                <PersonalInfo />
                <div className="selectInfoContainer">
                  <span className="selectInfoTitle">
                    선택적 개인정보.이용동의
                  </span>
                  <SelectInfo />
                  <div className="selectInfoCheck">
                    <input className="selectInfoBox" type="checkBox"></input>
                    <span className="selectInfoText">
                      [선택] 개인정보 수집.이용동의
                    </span>
                  </div>
                </div>
              </form>
            </div>
            <div className="signUpBtnBox">
              <span className="SignUpText">회원가입하기 (만 14세 이상)</span>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default SignUp;
