import React, { Component } from "react";
import { Link } from "react-router-dom";
import NavCategory from "./Components/NavCategory";
import NavList from "./Data/navList";
import Login from "../Login/Login";
import "./Nav.scss";

class Nav extends Component {
  constructor() {
    super();

    this.state = {
      mainCategory: [],
      isHover: false,
      isLogin: false,
      isLoginFail: false,
      isIdFail: false,
      isPwFail: false,
      userId: "",
      userPw: "",
    };
  }

  handleMouseOver = () => {
    const { isHover } = this.state;

    this.setState({
      isHover: !isHover,
    });
  };
  handleClick = () => {
    const { isLogin } = this.state;

    this.setState({
      isLogin: !isLogin,
    });
    console.log(isLogin);
  };

  handleClose = () => {
    this.setState({
      isLogin: false,
      isIdFail: false,
      isPwFail: false,
      userId: "",
      userPw: "",
    });
  };

  userInput = (e) => {
    const { userId, userPw, isIdFail } = this.state;
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });

    if (userId.length !== 0) {
      this.setState({
        isIdFail: false,
      });
    }
    if (userPw.length !== 0) {
      this.setState({
        isPwFail: false,
      });
    }
  };

  login = (e) => {
    e.preventDefault();
    const { userId, userPw } = this.state;
    if (userId.length === 0) {
      this.setState({
        isIdFail: true,
      });
    }
    if (userPw.length === 0) {
      this.setState({
        isPwFail: true,
      });
    }
    if (userId.length > 1 && userPw.length > 1) {
      alert("로그인성공");
    }
  };

  render() {
    const {
      isHover,
      isLogin,
      isLoginFail,
      isIdFail,
      isPwFail,
      userId,
      userPw,
      userInput,
      handleClose,
    } = this.state;
    return (
      <div className="headernav">
        <div className="Nav">
          <ul className="coLists">
            <li className="coList">
              <Link className="linkCo" to="/">
                <span className="linkName">Nike</span>
              </Link>
            </li>
            <li className="coList">
              <span className="linkCo" href="www">
                <span className="linkName">Join US</span>
              </span>
            </li>
            <li className="coList">
              <span
                className="linkCo"
                href="www"
                onMouseOver={this.handleMouseOver}
                onMouseOut={this.handleMouseOver}
              >
                <img
                  className="linkSvgJordan test"
                  src={
                    isHover
                      ? "./images/jordanLogoHover.svg"
                      : "./images/jordanLogo.svg"
                  }
                  alt="조던 연결 로고"
                />
              </span>
            </li>
            <li className="coList">
              <span className="linkCo" href="www">
                <img
                  className="linkSvgCon"
                  src="./images/converseLogo.svg"
                  alt="컨버스 로고"
                />
              </span>
            </li>
          </ul>
          <ul className="myMenuLists">
            <li className="myMenuList">
              <span className="linkMyMenuName">
                <Link className="linkMyMenuSign" to="/signup">
                  회원가입
                </Link>
                <span className="linkNull">/</span>
                <span className="linkMyMenuSign" onClick={this.handleClick}>
                  로그인
                </span>
              </span>
            </li>

            <li className="myMenuList">
              <span className="linkMyMenu" href="www">
                <span className="linkMyMenuName">고객센터</span>
              </span>
            </li>
            <li className="myMenuList">
              <span className="linkMyMenu" href="www">
                <img
                  className="myMenuCartImg"
                  src="./images/cartImg.svg"
                  alt="장바구니 아이콘"
                />
              </span>
            </li>
            <li className="myMenuList">
              <span className="linkMyMenu" href="www">
                <span className="linkMyMenuName">
                  <img
                    className="myMenuLocationImg"
                    src="./images/locationImg.svg"
                    alt="위치 아이콘"
                  />
                  대한민국
                </span>
              </span>
            </li>
          </ul>
        </div>
        <nav className="navMain">
          <Link className="mainLogoLink" to="/">
            <span className="mainLogoImg">
              <img src="./images/nikeMainLogo.svg" alt="메인 나이키 로고" />
            </span>
          </Link>
          <ul className="mainCategoryList">
            {NavList?.map((el, i) => {
              return (
                <NavCategory key={i} title={el.categoryTitle} data={el.data} />
              );
            })}
          </ul>
          <div className="searchContainer">
            <span className="mainInputName">
              <input className="mainInput" placeholder="검색" />
            </span>
            <button className="searchBtn">
              <img src="./images/searchBtn.svg" alt="검색 버튼" />
            </button>
          </div>
        </nav>
        <Login
          isLogin={isLogin}
          isLoginFail={isLoginFail}
          isIdFail={isIdFail}
          isPwFail={isPwFail}
          userId={userId}
          userPw={userPw}
          handleChange={this.userInput}
          handleClose={this.handleClose}
          loginP={this.login}
        />
      </div>
    );
  }
}
export default Nav;
