import React, { Component } from "react";
import "./ItemInfo.scss";

class ItemInfo extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      content: "",
      style: "",
      color: "",
    };
  }

  componentDidMount() {
    fetch("http://10.58.2.104:8000/product/2")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          title: result.data.title,
          content: result.data.content,
          style: result.data.style,
          color: result.data.color,
        });
      });
  }

  componentDidUpdate(prevProps, prevState) {
    const { style } = this.state;
    const { handleStyle } = this.props;
    if (prevState.style !== style) {
      handleStyle(style);
    }
  }

  render() {
    const { title, content, color, style } = this.state;
    return (
      <div className="ItemInfo">
        <div className="mainCopy">{title}</div>
        <div className="content">{content}</div>
        <div className="colorNstyle">
          <span className="color">{color}</span>
          <span className="style">스타일 : {style}</span>
          <span>더 보기 </span>
        </div>
      </div>
    );
  }
}

export default ItemInfo;
