import React, { Component } from "react";
import Nav from "../../Components/Nav/Nav";
import PersonalInfo from "./Components/PersonalInfo";
import TermsInfo from "./Components/TermsInfo";
import SelectInfo from "./Components/SelectInfo";

import "./SignUp.scss";

class SignUp extends Component {
  state = {
    userEmail: "",
    userPassword: "",
    userRePassword: "",
    userName: "",
    userPhone: "",
    userEmailActive: false,
    userPasswordActive: false,
    userRePasswordActive: false,
    userNameActive: false,
    userPhoneActive: false,
    userEmailError: false,
    userPasswordError: false,
    userRePasswordError: false,
    userNameError: false,
    userPhoneError: false,
  };

  handleSuc = (inputValue) => {
    const idError = inputValue.includes("@");

    if (idError) {
      console.log("AAA", inputValue);
      this.setState({
        userEmailActive: false,
      });
    }
  };

  handleActive = (inputError, inputValue) => {
    const {
      userPassword,
      userEmailError,
      userPasswordError,
      userRePasswordError,
      userNameError,
      userPhoneError,
    } = this.state;
    const basicError = inputValue.length;
    const checkSpc = /[~!@#$%^&*()_+|<>?:{}]/;
    const checkKor = /[ㄱ-ㅎ|ㅏ-ㅣ|가-힣]/;
    const checkEng = /[a-zA-Z]/;
    const checkNum = /[0-9]/;

    const idError = inputValue.includes("@") && inputValue.includes(".");
    // const pwError =
    //   userPassword &&
    //   checkNum(userPassword) &&
    //   !checkKor(userPassword) &&
    //   checkEng(userPassword) &&
    //   checkSpc(userPassword);

    if (basicError === 0) {
      this.setState({
        [`${inputError}Active`]: true,
        // userEmailError: false,
        [`${inputError}Error`]: false,
        // userPasswordError: false,
      });
    } else if (basicError > 1) {
      if (idError) {
        this.setState({
          [`${inputError}Active`]: false,
        });
      } else if (!idError) {
        this.setState({
          [`${inputError}Active`]: true,
          // userEmailError: true,
          [`${inputError}Error`]: true,
        });
      }
      // else if (pwError) {
      //   this.setState({
      //     [`${inputError}Active`]: false,
      //   });
      // } else if (!pwError) {
      //   this.setState({
      //     [`${inputError}Active`]: true,
      //     userEmailError: true,
      //   });
      // }
    }
  };

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState(
      {
        [name]: value,
      },
      () => {
        this.handleActive(name, value);
      }
    );
  };

  handleClick = () => {
    // this.setState
  };

  render() {
    const {
      userEmailActive,
      userPasswordActive,
      userRePasswordActive,
      userNameActive,
      userPhoneActive,
      userEmailError,
      userPasswordError,
      userRePasswordError,
      userNameError,
      userPhoneError,
    } = this.state;

    return (
      <div className="SignUp">
        <Nav />
        <section className="section">
          <div className="signUpCenter">
            <div className="signUpText">
              <h2>나이키 멤버 가입</h2>
              <p>
                멤버가 되어 나이키가 제공하는<br></br> 최고의 제품과 혜택을
                만나보세요.
              </p>
            </div>
            <div className="coSignConnect">
              <span className="kakaoSignUp">
                <img
                  className="kakaoLogo"
                  src="./images/kakaotalk.svg"
                  alt="카카오톡 이미지"
                />
                카카오 계정으로 신규 가입
              </span>
              <span className="faceBookSignUp">
                <img
                  className="faceBookLogo"
                  src="./images/faceBook.svg"
                  alt="페이스북 이미지"
                />
                페이스북 계정으로 신규 가입
              </span>
            </div>
            <div className="orContainer">
              <div className="orLine"></div>
              <span className="orText">OR</span>
              <div className="orLine"></div>
            </div>
            <div className="infoContainer">
              <form className="infoForm">
                <div className="inputCenter">
                  <input
                    className={userEmailActive ? "inputBoxRed" : "inputBox"}
                    placeholder="사용하실 ID를 입력해주세요. (수신 가능 E-mail)"
                    name="userEmail"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={userEmailActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {this.state.userEmailError
                      ? "이메일 형태로 입력해주세요. 해당 계정으로 주문 내역이 발송됩니다."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={userPasswordActive ? "inputBoxRed" : "inputBox"}
                    placeholder="영문+숫자+특수문자 8~16자리(특수문자 괄호()는 사용 불가)"
                    name="userPassword"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={userPasswordActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {this.state.userPasswordError
                      ? "영문/숫자/특수문자 조합 8~16자 조합으로 입력해주세요."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={
                      userRePasswordActive ? "inputBoxRed" : "inputBox"
                    }
                    placeholder="패스워드를 다시 입력해주세요."
                    name="userRePassword"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={
                      userRePasswordActive ? "errorMsg" : "errorMsgNone"
                    }
                  >
                    {this.state.userRePasswordError
                      ? "입력값이 일차히자 않습니다."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={userNameActive ? "inputBoxRed" : "inputBox"}
                    placeholder="이름을 입력해 주세요."
                    name="userName"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={userNameActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {this.state.userNameError
                      ? "한글만 입력 가능합니다."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <div className="inputCenter">
                  <input
                    className={userPhoneActive ? "inputBoxRed" : "inputBox"}
                    placeholder="휴대폰 번호 '-'표 없이 입력해 주세요."
                    name="userPhone"
                    onChange={this.handleChange}
                  ></input>
                  <span
                    className={userPhoneActive ? "errorMsg" : "errorMsgNone"}
                  >
                    {this.state.userPhoneError
                      ? "숫자만 입력 가능합니다."
                      : "필수 입력 항목입니다."}
                  </span>
                </div>
                <TermsInfo />
                <PersonalInfo />
                <div className="selectInfoContainer">
                  <span className="selectInfoTitle">
                    선택적 개인정보.이용동의
                  </span>
                  <SelectInfo />
                  <div className="selectInfoCheck">
                    <input className="selectInfoBox" type="checkBox"></input>
                    <span className="selectInfoText">
                      [선택] 개인정보 수집.이용동의
                    </span>
                  </div>
                </div>
              </form>
            </div>
            <div className="signUpBtnBox">
              <button className="SignUpText" onClick={this.handleClick}>
                회원가입하기 (만 14세 이상)
              </button>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default SignUp;
