import React, { Component } from "react";
import DetailImages from "./Components/DetailImages";
import ProductInfo from "./Components/ProductInfo";
import RecommendItem from "./Components/Recommand/RecommenItem";
import Nav from "../../Components/Nav/Nav";
import Footer from "../../Components/Footer/Footer";
import MoveToTop from "../../Components/moveToTop/MoveToTop";
import "./ItemDetail.scss";

class ItemDetail extends Component {
  constructor() {
    super();
    this.state = {
      detailImage: [],
      modal: false,
    };
  }

  handleModal = (index) => {
    const { modal } = this.state;
    this.setState({
      modal: !modal,
      imgCurrent: index,
    });
  };

  componentDidMount() {
    fetch(`http://10.58.2.104:8000/product/5`)
      .then((res) => res.json())
      .then((result) => {
        this.setState({ detailImage: result.data.detailImage });
      });
  }

  render() {
    const { detailImage, modal } = this.state;
    return (
      <>
        <Nav />
        <article className="ItemDetail">
          <DetailImages
            handleModal={this.handleModal}
            detailImage={detailImage}
            modal={modal}
          />
          <ProductInfo detailImage={detailImage} />
          <RecommendItem />
          <MoveToTop />
        </article>
        <Footer />
      </>
    );
  }
}

export default ItemDetail;
