import React, { Component } from "react";
import "./Modal.scss";

class Modal extends Component {
  constructor() {
    super();
    this.state = {
      Image: [],
    };
  }

  componentDidMount() {
    fetch("/Data/itemDetail/DetailData.json")
      .then((res) => res.json())
      .then((result) => {
        this.setState({ Image: result.image });
      });
  }

  render() {
    const { Image } = this.state;
    const { modal, handleModal } = this.props;
    return (
      <div className={modal ? "activeModal" : "noModal"} onClick={handleModal}>
        {Image.map((el, idx) => (
          <img key={idx} src={el} alt="DetailImage" />
        ))}
        <img
          src="./images/ItemDetail/cross.png"
          alt="exitImage"
          className="exit"
        />
      </div>
    );
  }
}

export default Modal;
