import React, { Component } from "react";
import CoLinkName from './Components/CoLinkName'
import "./Nav.scss"

class Nav extends Component {
  constructor() {
    super();
    this.state = {
      linkCoName: [],
      mainCategory: [],
    }
  }
  componentDidMount() {
    // 코퍼레이션 링크
    fetch('http://localhost:3000/data/LinkCoData.json', {
      method: 'GET'
    })
      .then(res => res.json())
      .then(res => {
        this.setState({
          linkCoName: res.LinkCoData
        });
      });
      // 메인 카테고리 데이터
      fetch('http://localhost:3000/data/MainCategory.json', {
      method: 'GET'
    })
      .then(res => res.json())
      .then(res => {
        this.setState({
          mainCategory: res.MainCategory
        });
      });
  }
  render() {
    return (
    <header className="Nav">
      <div className="header">
         <ul className="coLists">    
          {this.state.linkCoName.map(el => 
            <CoLinkName 
            key={el.id}
            linkDataId={el.id}
            linkDataName={el.coName} />
            )}
         </ul>

          <ul className="myMenuLists">
            <li className="myMenuList">

                <span className="linkMyMenuName">
                  <a className="linkMyMenu" href="www">회원가입</a> 
                  <span className="linkNull" >/</span>
                  <a className="linkMyMenu" href="www">로그인</a>
                </span>

            </li>
            <li className="myMenuList">
              <a className="linkMyMenu" href="www">
                <span className="linkMyMenuName">
                  고객센터
                </span>
              </a>
            </li>
            <li className="myMenuList">
              <a className="linkMyMenu" href="www">
                <span className="linkMyMenuName">
                  장바구니 아이콘
                </span>
              </a>
            </li>
            <li className="myMenuList">
              <a className="linkMyMenu" href="www">
                <span className="linkMyMenuName">
                  위치
                </span>
              </a>
            </li>
          </ul>
        </div>
      <nav className="navMain">

        <a className="mainLogoLink">
          <span className="mainLogoImg">
            나이키
          </span>
        </a>

        <ul className="MainCategoryList">
          <li className="MainCategoryitem">
            <a>
            NEW RELLEASES
            </a>
          </li>
          <li>
            <a>
           MEN
            </a>
          </li>
          <li>
            <a>
            WOMEN
            </a>
          </li>
          <li>
            <a>
            KIDS
            </a>
          </li>
        </ul>

        <span><input></input></span>

      </nav>

    </header>
    )
  }
}
export default Nav;
