import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import NavCategory from "./Components/NavCategory";
import NavList from "./Data/navList";
import Login from "../Login/Login";
import "./Nav.scss";

class Nav extends Component {
  state = {
    mainCategory: [],
    jordanHover: false,
    loginModal: false,
    IdInvalid: false,
    PwInvalid: false,
    userId: "",
    userPw: "",

    loginStatus: false,
    myInfoDropDown: false,
  };

  userDropdown = () => {
    const { myInfoDropDown } = this.state;
    this.setState({
      myInfoDropDown: !myInfoDropDown,
    });
  };

  handleEsc = (e) => {
    if (e.key === "Escape") {
      this.handleClose();
    }
  };

  handleMouseOver = () => {
    const { jordanHover } = this.state;

    this.setState({
      jordanHover: !jordanHover,
    });
  };

  handleClick = () => {
    const { loginModal } = this.state;

    this.setState({
      loginModal: !loginModal,
    });
  };

  handleClose = () => {
    this.setState({
      loginModal: false,
      IdInvalid: false,
      PwInvalid: false,
      userId: "",
      userPw: "",
    });
  };

  userInput = (e) => {
    const { userId, userPw } = this.state;
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });

    if (userId.length !== 0) {
      this.setState({
        IdInvalid: false,
      });
    }

    if (userPw.length !== 0) {
      this.setState({
        PwInvalid: false,
      });
    }
  };

  gotoSignup = () => {
    this.props.history.push("/signup");
    window.scrollTo(0, 0);
  };

  login = (e) => {
    e.preventDefault();
    const { userId, userPw } = this.state;
    if (userId.length === 0) {
      this.setState({
        IdInvalid: true,
      });
    }

    if (userPw.length === 0) {
      this.setState({
        PwInvalid: true,
      });
    }

    if (userId.length > 1 && userPw.length > 1) {
      fetch("http://10.58.3.208:8000/users/signin", {
        method: "POST",
        body: JSON.stringify({
          email: userId,
          password: userPw,
        }),
      })
        .then((response) => response.json())
        .then((response) => {
          if (response.Authorization) {
            console.log("su", response);
            localStorage.setItem("token", response.Authorization);
            alert("로그인 성공");
            this.setState(
              {
                loginStatus: true,
                loginModal: false,
              },
              this.props.history.push("/")
            );
          } else {
            console.log("wr", response);
            alert("잘못된 비밀번호입니다. 다시 입력하세요.");
          }
        });
    }
  };

  render() {
    const {
      jordanHover,
      loginModal,
      IdInvalid,
      PwInvalid,
      userId,
      userPw,
      loginStatus,
      myInfoDropDown,
    } = this.state;
    return (
      <div className="Nav">
        <div className="headernav">
          <ul className="coLists">
            <li className="coList">
              <Link className="linkCo" to="/">
                <span className="linkName">Nike</span>
              </Link>
            </li>
            <li className="coList">
              <span className="linkCo">
                <span className="linkName">Join US</span>
              </span>
            </li>
            <li className="coList">
              <span
                className="linkCo"
                onMouseOver={this.handleMouseOver}
                onMouseOut={this.handleMouseOver}
              >
                <img
                  className="linkSvgJordan test"
                  src={
                    jordanHover
                      ? "./images/Nav/jordanLogoHover.svg"
                      : "./images/Nav/jordanLogo.svg"
                  }
                  alt="조던 연결 로고"
                />
              </span>
            </li>
            <li className="coList">
              <span className="linkCo">
                <img
                  className="linkSvgCon"
                  src="./images/Nav/converseLogo.svg"
                  alt="컨버스 로고"
                />
              </span>
            </li>
          </ul>
          <ul className="myMenuLists">
            <li className="myMenuList">
              <span
                className={
                  loginStatus ? "linkMyInfos" : "linkMyInfos linkMyInfoHide"
                }
              >
                <div className="userMenuTab" onClick={this.userDropdown}>
                  <i className="fas fa-user-circle myInfoIcon"></i>
                  <span>나위키</span>
                </div>
                <div
                  className={
                    myInfoDropDown ? "userDropDown " : "userDropDownHide"
                  }
                >
                  <span className="myInfoMenuList">마이페이지</span>
                  <span className="myInfoMenuList">회원정보관리</span>
                  <span className="myInfoMenuList">위시리스트</span>
                  <span className="myInfoMenuList">로그아웃</span>
                </div>
              </span>
              <span
                className={loginStatus ? "linkMyInfoHide" : "linkMyMenuName"}
              >
                <span className="linkMyMenuSign" onClick={this.gotoSignup}>
                  회원가입
                </span>
                <span className="linkNull">/</span>
                <span className="linkMyMenuSign" onClick={this.handleClick}>
                  로그인
                </span>
              </span>
            </li>

            <li className="myMenuList">
              <span className="linkMyMenu">
                <span className="linkMyMenuName">고객센터</span>
              </span>
            </li>
            <li className="myMenuList">
              <span className="linkMyMenu">
                <img
                  className="myMenuCartImg"
                  src="./images/Nav/cartImg.svg"
                  alt="장바구니 아이콘"
                />
              </span>
            </li>
            <li className="myMenuList">
              <span className="linkMyMenu">
                <span className="linkMyMenuName">
                  <img
                    className="myMenuLocationImg"
                    src="./images/Nav/locationImg.svg"
                    alt="위치 아이콘"
                  />
                  대한민국
                </span>
              </span>
            </li>
          </ul>
        </div>
        <nav className="navMain">
          <Link className="mainLogoLink" to="/">
            <span className="mainLogoImg">
              <img src="./images/Nav/nikeMainLogo.svg" alt="메인 나이키 로고" />
            </span>
          </Link>
          <ul className="mainCategoryList">
            {NavList?.map((el, i) => {
              return (
                <NavCategory key={i} title={el.categoryTitle} data={el.data} />
              );
            })}
          </ul>
          <div className="searchContainer">
            <span className="mainInputName">
              <input className="mainInput" placeholder="검색" />
            </span>
            <button className="searchBtn">
              <img src="./images/Nav/searchBtn.svg" alt="검색 버튼" />
            </button>
          </div>
        </nav>
        <Login
          loginModal={loginModal}
          IdInvalid={IdInvalid}
          PwInvalid={PwInvalid}
          userId={userId}
          userPw={userPw}
          handleChange={this.userInput}
          handleClose={this.handleClose}
          loginP={this.login}
          handleEsc={this.handleEsc}
        />
      </div>
    );
  }
}

export default withRouter(Nav);
