import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import NavCategory from "./Components/NavCategory";
import NavList from "./Data/navList";
import Login from "../Login/Login";
import "./Nav.scss";

class Nav extends Component {
  state = {
    mainCategory: [],
    isHover: false,
    isLogin: false,
    isLoginFail: false,
    isIdFail: false,
    isPwFail: false,
    userId: "",
    userPw: "",

    userLogin: false,
  };

  handleEsc = (e) => {
    if (e.key === "Escape") {
      this.handleClose();
    }
  };

  handleMouseOver = () => {
    const { isHover } = this.state;

    this.setState({
      isHover: !isHover,
    });
  };
  handleClick = () => {
    const { isLogin } = this.state;

    this.setState({
      isLogin: !isLogin,
    });
  };

  handleClose = () => {
    this.setState({
      isLogin: false,
      isIdFail: false,
      isPwFail: false,
      userId: "",
      userPw: "",
    });
  };

  userInput = (e) => {
    const { userId, userPw } = this.state;
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });

    if (userId.length !== 0) {
      this.setState({
        isIdFail: false,
      });
    }

    if (userPw.length !== 0) {
      this.setState({
        isPwFail: false,
      });
    }
  };

  gotoSignup = () => {
    this.props.history.push("/signup");
    window.scrollTo(0, 0);
  };

  login = (e) => {
    e.preventDefault();
    const { userId, userPw } = this.state;
    if (userId.length === 0) {
      this.setState({
        isIdFail: true,
      });
    }

    if (userPw.length === 0) {
      this.setState({
        isPwFail: true,
      });
    }

    if (userId.length > 1 && userPw.length > 1) {
      alert("로그인성공");
      fetch("http://10.58.3.208:8000/users/signin", {
        method: "POST",
        body: JSON.stringify({
          email: userId,
          password: userPw,
        }),
      })
        .then((response) => response.json())
        .then((result) => {
          console.log(result);
          console.log(localStorage.getItem("token"));
        });
      this.props.history.push("/");

      if (result.message === "SUCCESS") {
        alert("로그인 성공");
        this.setState({
          userLogin: true,
        });
      } else if (result.message === "DUPLICATED_EMAIL") {
        alert("중복된 이메일이 있습니다.");
      }
      // if (result.Authorization) {
      //   localStorage.setItem("token", result.Authorization);
      //   alert("로그인 성공");
      // } else if (result.message === "UNAUTHORIZED") {
      //   alert("비밀번호를 확인해주세요.");
      // }
    }
  };

  render() {
    const {
      isHover,
      isLogin,
      isLoginFail,
      isIdFail,
      isPwFail,
      userId,
      userPw,
      userLogin,
    } = this.state;
    return (
      <div className="Nav">
        <div className="headernav">
          <ul className="coLists">
            <li className="coList">
              <Link className="linkCo" to="/">
                <span className="linkName">Nike</span>
              </Link>
            </li>
            <li className="coList">
              <span className="linkCo">
                <span className="linkName">Join US</span>
              </span>
            </li>
            <li className="coList">
              <span
                className="linkCo"
                onMouseOver={this.handleMouseOver}
                onMouseOut={this.handleMouseOver}
              >
                <img
                  className="linkSvgJordan test"
                  src={
                    isHover
                      ? "./images/Nav/jordanLogoHover.svg"
                      : "./images/Nav/jordanLogo.svg"
                  }
                  alt="조던 연결 로고"
                />
              </span>
            </li>
            <li className="coList">
              <span className="linkCo">
                <img
                  className="linkSvgCon"
                  src="./images/Nav/converseLogo.svg"
                  alt="컨버스 로고"
                />
              </span>
            </li>
          </ul>
          <ul className="myMenuLists">
            <li className="myMenuList">
              <span className={userLogin ? "linkMyInfo" : "linkMyInfoHide"}>
                <i class="fas fa-user-circle myInfoIcon"></i>
                <span>나이키</span>
              </span>
              <span className={userLogin ? "linkMyInfoHide" : "linkMyMenuName"}>
                <span className="linkMyMenuSign" onClick={this.gotoSignup}>
                  회원가입
                </span>
                <span className="linkNull">/</span>
                <span className="linkMyMenuSign" onClick={this.handleClick}>
                  로그인
                </span>
              </span>
            </li>

            <li className="myMenuList">
              <span className="linkMyMenu">
                <span className="linkMyMenuName">고객센터</span>
              </span>
            </li>
            <li className="myMenuList">
              <span className="linkMyMenu">
                <img
                  className="myMenuCartImg"
                  src="./images/Nav/cartImg.svg"
                  alt="장바구니 아이콘"
                />
              </span>
            </li>
            <li className="myMenuList">
              <span className="linkMyMenu">
                <span className="linkMyMenuName">
                  <img
                    className="myMenuLocationImg"
                    src="./images/Nav/locationImg.svg"
                    alt="위치 아이콘"
                  />
                  대한민국
                </span>
              </span>
            </li>
          </ul>
        </div>
        <nav className="navMain">
          <Link className="mainLogoLink" to="/">
            <span className="mainLogoImg">
              <img src="./images/Nav/nikeMainLogo.svg" alt="메인 나이키 로고" />
            </span>
          </Link>
          <ul className="mainCategoryList">
            {NavList?.map((el, i) => {
              return (
                <NavCategory key={i} title={el.categoryTitle} data={el.data} />
              );
            })}
          </ul>
          <div className="searchContainer">
            <span className="mainInputName">
              <input className="mainInput" placeholder="검색" />
            </span>
            <button className="searchBtn">
              <img src="./images/Nav/searchBtn.svg" alt="검색 버튼" />
            </button>
          </div>
        </nav>
        <Login
          isLogin={isLogin}
          isLoginFail={isLoginFail}
          isIdFail={isIdFail}
          isPwFail={isPwFail}
          userId={userId}
          userPw={userPw}
          handleChange={this.userInput}
          handleClose={this.handleClose}
          loginP={this.login}
          handleEsc={this.handleEsc}
        />
      </div>
    );
  }
}

export default withRouter(Nav);
